package com.arrays;

import java.util.HashSet;
import java.util.Set;

public class VowelSubArrays {

	public static void main(String[] args) {
		System.out.println(new VowelSubArrays().solve(
				""));
	}

	public int solve(String A) {

		Set<Character> sets = new HashSet<>();

		sets.add('a');
		sets.add('e');
		sets.add('i');
		sets.add('o');
		sets.add('u');
		sets.add('A');
		sets.add('E');
		sets.add('I');
		sets.add('O');
		sets.add('U');

		int cnt = 0;
		int pos = 0;

		for (int i = A.length() - 1; i >= 0; i--) {

			if (sets.contains(A.charAt(i))) {
				
				System.out.println(A.charAt(i)+" "+i+" "+(A.length() - i));
				
				cnt = cnt + A.length() - i;
			}

		}

		return cnt;
	}

}
